import logo from './logo.svg';
import React, { useState } from 'react';

// import './static/css/main.css';
// import 'Searchs_130.png';
const csrftoken = getCookie('csrftoken');
   console.log(csrftoken);
function App() {
  const [name,setName] = useState('Chint');
  function formHandler(e){
    e.preventDefault();
    // setName('Chintan');
    console.log('Here');
    var link = document.getElementById('search').value;

   function getCookie(name) {
       let cookieValue = null;
       if (document.cookie && document.cookie !== '') {
           const cookies = document.cookie.split(';');
           for (let i = 0; i < cookies.length; i++) {
               const cookie = cookies[i].trim();
               // Does this cookie string begin with the name we want?
               if (cookie.substring(0, name.length + 1) === (name + '=')) {
                   cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                   break;
               }
           }
       }
       return cookieValue;
   }


        fetch('http://localhost:8000/post-link', {
          method: 'POST',
          headers: {

            Accept: 'application/json',
            'Content-Type': 'application/json',
            'X-CSRFToken': csrftoken
          },
          body: JSON.stringify({
            link: link,

          })
        })
        .then((response) => response.json())
            .then((response) => {
              console.log(response);
              return response;
            })
            .catch((error) => {
              console.error(error);
            });

  }
  return (
    <div className="s130">
          <form onSubmit={formHandler} >
          <input type="hidden" name="csrfmiddlewaretoken" value={csrftoken} />
            <div className="inner-form">
              <div className="input-field first-wrap">
                <div className="svg-wrapper">
                  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                    <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"></path>
                  </svg>
                </div>
                <input id="search" type="text" placeholder="Paste the link to be shortened ..." name="link" value={name} />
              </div>
              <div className="input-field second-wrap">
                <button className="btn-search" type="submit">SHORTEN</button>
              </div>
            </div>

          </form>

        </div>
  );
}

export default App;
